// In-memory storage for new properties (simulates database)
let properties: Property[] = [...mockProperties];

export function getProperties(): Property[] {
    return properties;
}

export function getPropertyById(id: string): Property | undefined {
    return properties.find((p) => p.id === id);
}

export function addProperty(property: Omit<Property, "id" | "createdAt" | "updatedAt">): Property {
    const newProperty: Property = {
        ...property,
        id: `property-${Date.now()}`,
        createdAt: new Date(),
        updatedAt: new Date(),
    };
    properties.push(newProperty);
    return newProperty;
}

export function updateProperty(id: string, updates: Partial<Property>): Property | undefined {
    const index = properties.findIndex((p) => p.id === id);
    if (index === -1) return undefined;

    properties[index] = {
        ...properties[index],
        ...updates,
        updatedAt: new Date(),
    };
    return properties[index];
}

export function deleteProperty(id: string): boolean {
    const index = properties.findIndex((p) => p.id === id);
    if (index === -1) return false;

    properties.splice(index, 1);
    return true;
}
